-- ----------------------------
-- Table structure for forum_answer
-- ----------------------------
DROP TABLE IF EXISTS `forum_answer`;
CREATE TABLE `forum_answer`  (
  `answer_id` bigint NOT NULL COMMENT '回答ID',
  `question_id` bigint NOT NULL COMMENT '问题ID',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `content_markdown` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '原始的正文内容',
  `content_rendered` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '过滤渲染后的正文内容',
  `comment_count` int UNSIGNED NULL DEFAULT 0 COMMENT '评论数量',
  `vote_count` int NULL DEFAULT 0 COMMENT '投票数，赞成票-反对票，可以为负数',
  `vote_up_count` int NULL DEFAULT 0 COMMENT '赞成票总数',
  `vote_down_count` int NULL DEFAULT 0 COMMENT '反对票总数',
  `create_time` date NOT NULL COMMENT '创建时间',
  `update_time` date NULL DEFAULT NULL COMMENT '更新时间',
  `delete_time` date NULL DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`answer_id`) USING BTREE,
  INDEX `question_id`(`question_id` ASC) USING BTREE,
  INDEX `user_id`(`user_id` ASC) USING BTREE,
  INDEX `vote_count`(`vote_count` ASC) USING BTREE,
  INDEX `create_time`(`create_time` ASC) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '回答表' ROW_FORMAT = Dynamic;

