-- ----------------------------
-- Table structure for forum_article
-- ----------------------------
DROP TABLE IF EXISTS `forum_article`;
CREATE TABLE `forum_article`  (
  `article_id` bigint NOT NULL COMMENT '文章ID',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `title` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '标题',
  `content_markdown` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '原始的正文内容',
  `content_rendered` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '过滤渲染后的正文内容',
  `comment_count` int UNSIGNED NULL DEFAULT 0 COMMENT '评论数量',
  `follower_count` int UNSIGNED NULL DEFAULT 0 COMMENT '关注者数量',
  `vote_count` int NULL DEFAULT 0 COMMENT '投票数，赞成票-反对票，可以为负数',
  `vote_up_count` int NULL DEFAULT 0 COMMENT '赞成票总数',
  `vote_down_count` int NULL DEFAULT 0 COMMENT '反对票总数',
  `update_time` date NULL DEFAULT NULL COMMENT '更新时间',
  `delete_time` date NULL DEFAULT NULL COMMENT '删除时间',
  `audit_status` int NOT NULL DEFAULT 0 COMMENT '审核状态 默认0,审核通过1,未通过2,通过并置顶3',
  `delete_status` int NULL DEFAULT 0 COMMENT '是否被删除',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`article_id`) USING BTREE,
  INDEX `user_id`(`user_id` ASC) USING BTREE,
  INDEX `create_time`(`create_time` ASC) USING BTREE,
  INDEX `vote_count`(`vote_count` ASC) USING BTREE,
  CONSTRAINT `forum_article_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `forum_user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '文章表' ROW_FORMAT = Dynamic;

