DROP TABLE IF EXISTS `forum_audit`;
CREATE TABLE `forum_audit`  (
  `audit_id` bigint NOT NULL COMMENT '审核id',
  `audit_type` int NOT NULL COMMENT '审核的类型(文章,问题等)',
  `audited_id` bigint NOT NULL COMMENT '审核目标的id',
  `audit_man_id` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '审核人id',
  `audit_starus` int UNSIGNED NOT NULL DEFAULT 0 COMMENT '审核状态(0未审核,1审核通过,2审核未通过)',
  `audit_date` date NULL DEFAULT NULL COMMENT '审核时间',
  `audit_reason` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '审核原因',
  PRIMARY KEY (`audit_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

