CREATE TABLE `forum_cache`  (
  `name` varchar(180) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `value` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `create_time` date NULL DEFAULT NULL COMMENT '创建时间',
  `life_time` int UNSIGNED NOT NULL DEFAULT 0 COMMENT '有效时间',
  PRIMARY KEY (`name`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '缓存表' ROW_FORMAT = Dynamic;

