-- Records of forum_cache
-- ----------------------------

-- ----------------------------
-- Table structure for forum_comment
-- ----------------------------
DROP TABLE IF EXISTS `forum_comment`;
CREATE TABLE `forum_comment`  (
  `comment_id` bigint NOT NULL COMMENT '回答评论ID',
  `commentable_id` bigint UNSIGNED NOT NULL COMMENT '评论目标的ID',
  `commentable_type` int NOT NULL COMMENT '评论目标类型：article、question、answer、comment',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '原始正文内容',
  `reply_count` int UNSIGNED NULL DEFAULT 0 COMMENT '回复数量',
  `vote_count` int UNSIGNED NULL DEFAULT 0 COMMENT '投票数，赞成票-反对票，可以为负数',
  `vote_up_count` int UNSIGNED NULL DEFAULT 0 COMMENT '赞成票总数',
  `vote_down_count` int UNSIGNED NULL DEFAULT 0 COMMENT '反对票总数',
  `create_time` date NOT NULL COMMENT '创建时间',
  `update_time` date NULL DEFAULT NULL COMMENT '更新时间',
  `delete_time` date NULL DEFAULT NULL COMMENT '删除时间',
  `parent_id` bigint NULL DEFAULT NULL COMMENT '父评论ID',
  PRIMARY KEY (`comment_id`) USING BTREE,
  INDEX `user_id`(`user_id` ASC) USING BTREE,
  INDEX `commentable_id`(`commentable_id` ASC) USING BTREE,
  INDEX `create_time`(`create_time` ASC) USING BTREE,
  INDEX `vote_count`(`vote_count` ASC) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '回答评论表' ROW_FORMAT = Dynamic;

