  `create_time` date NOT NULL COMMENT '发送时间',
  `read_time` date NOT NULL COMMENT '阅读时间',
  PRIMARY KEY (`notification_id`) USING BTREE,
  INDEX `receiver_id`(`receiver_id` ASC) USING BTREE,
  INDEX `sender_id`(`sender_id` ASC) USING BTREE,
