  INDEX `question_id`(`question_id` ASC) USING BTREE,
  INDEX `answer_id`(`answer_id` ASC) USING BTREE,
  INDEX `comment_id`(`comment_id` ASC) USING BTREE,
  CONSTRAINT `forum_notification_ibfk_1` FOREIGN KEY (`receiver_id`) REFERENCES `forum_user` (`user_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `forum_notification_ibfk_2` FOREIGN KEY (`sender_id`) REFERENCES `forum_user` (`user_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `forum_notification_ibfk_3` FOREIGN KEY (`article_id`) REFERENCES `forum_article` (`article_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `forum_notification_ibfk_4` FOREIGN KEY (`question_id`) REFERENCES `forum_question` (`question_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `forum_notification_ibfk_5` FOREIGN KEY (`answer_id`) REFERENCES `forum_answer` (`answer_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `forum_notification_ibfk_6` FOREIGN KEY (`comment_id`) REFERENCES `forum_comment` (`comment_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '通知表' ROW_FORMAT = Dynamic;

