DROP TABLE IF EXISTS `forum_question`;
CREATE TABLE `forum_question`  (
  `question_id` bigint NOT NULL COMMENT '问题ID',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `title` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '标题',
  `content_markdown` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '原始的正文内容',
  `content_rendered` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '过滤渲染后的正文内容',
  `comment_count` int UNSIGNED NULL DEFAULT 0 COMMENT '评论数量',
  `answer_count` int UNSIGNED NULL DEFAULT 0 COMMENT '回答数量',
  `follower_count` int UNSIGNED NULL DEFAULT 0 COMMENT '关注者数量',
  `vote_count` int NULL DEFAULT 0 COMMENT '投票数，赞成票-反对票，可以为负数',
